class CanaryTest extends GroovyTestCase
{
    void testRegex()
    {
        def str = 'gataca'

        def m = ( str =~ /t(a)c/ )

        println m
        assert m.count
        println m[0]
        println m[0][1]
    }

    void testConstructor()
    {
        def can = new Canary()
    }


    void testRunCanary()
    {


        def can = new Canary()

        String resource = "Canary/Sub"
        String file = "CancerGNA10212011_170_190_Viewermanifest"
        String extension ="fasta"
        File ampfile = new File(CanaryTest.getClass().getResource( "/${resource}/${file}.${extension}" ).getPath())

        String amplist = "KIT24_28.chr4.55599320.55599348_tile_1, KDR1.chr4.55946114.55946257_tile_1, GNAQ_5.chr9.80409379.80409508_tile_1, PTEN13.chr10.89720716.89720852_tile_1, PTEN13.chr10.89720716.89720852_tile_1"

        file = "CancerGNA10212011_170_190_Viewermanifest"
        extension = "fasta.primers"
        File primfile = new File(CanaryTest.getClass().getResource( "/${resource}/${file}.${extension}" ).getPath())

        def vars = []

        String basepath = primfile.getParent()
        File ofile = new File("${basepath}/canary.out")

        file = "canary"
        extension = "vcf"
        File vcffile = new File(CanaryTest.getClass().getResource( "/${resource}/${file}.${extension}" ).getPath())

        file = "14M6168_AACCCCTC-TAGACCTA_L001_R1_001"
        extension = "fastq.gz"
        File r1 = new File(CanaryTest.getClass().getResource( "/${resource}/${file}.${extension}" ).getPath())


        file = "14M6168_AACCCCTC-TAGACCTA_L001_R2_001"
        extension = "fastq.gz"
        File r2 = new File(CanaryTest.getClass().getResource( "/${resource}/${file}.${extension}" ).getPath())

        Double pct = 0.0

       assert can.runCanary(  ampfile,  amplist,  primfile, vars,  ofile,  vcffile,  r1,  r2,  pct ) == 2000
    }

