    void testRegExp()
    {

        String bases = "gataca"
        boolean dir = false
        int offset = 0

        Map var = ["chr":"4","pos": "55599436","ref": "T","alt": "C", "chr_pos_ref/alt":"13/13", "hgvsg":"chr4:g.55599436T>C"]


        def can = new Canary()

       assert  can.regex(  bases,  offset,  dir,  var ).toString() == "ataca(A|G)ataca"
    }

    void testSAMFileWriter()
    {
        def can = new Canary()

        String bamFile =  "BamOut.bam"
        String sample = "14M5511"
        String readGroup = "ID"

        //
        // This returns a string with the initial pattern as  htsjdk.samtools.BAMFileWriter@...
        assert can.openBam(  bamFile,  sample,  readGroup ).toString().split("@")[0] == "htsjdk.samtools.BAMFileWriter"

    }

